import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {
    handelCheck = (eve,id) =>{

        console.log(id,eve.target.checked)
        this.props.updateTodo(id,eve.target.checked)

    }


    render() {
        const {id,name,done} = this.props
        return (
            <li>
                <label>
                    <input type="checkbox" checked={done} onChange={(event)=>this.handelCheck(event,id)}/>
                    <span>{name}</span>
                </label>
                <button className="btn btn-danger" >DEL</button>
            </li>
        )
    }
}
